function enqueue_files() {
	if ( is_page( 'contact-us' ) ) {
		wp_enqueue_script( 'mpixels-contac-us-page-script', get_template_directory_uri() . '/js/mellowpxls_contacts_form.js', array('jquery'),'', true );

		wp_localize_script( 'mpixels-contac-us-page-script', 'localized_data', array(
			'ajax_url' => admin_url( 'admin-ajax.php' )
		));
	}

	wp_register_script(
		"angularjs",
		"https://code.angularjs.org/1.6.1/angular.min.js"
	);
	
	wp_enqueue_script(
		'match-media',
		get_template_directory_uri() . '/js/angular-media-queries/match-media.js',
		array( 'angularjs' )
	);

	wp_enqueue_script(
		'my-scripts',
		get_template_directory_uri() . '/js/ng-script.js',
		array( 'angularjs' )
	);


	if (is_front_page()) {
		wp_enqueue_script(
			"homepage_setup",
			get_template_directory_uri() . '/js/mellowpixels.main.js',
			array('jquery'),'', true
		);
	}
}
add_action( 'wp_enqueue_scripts', 'enqueue_files' );


function processContactFormData(){
	if(isset($_POST["action"]) && $_POST["action"] == "processContactFormData"){

		$sender_data = array();
		$status_msg = "";

		if( isset($_POST["name"]) ){
			
			$sender_data["sender_name"] = strip_tags($_POST["name"]);

			if( isset($_POST["email"]) ){

				$sender_data["sender_email"] = strip_tags($_POST["email"]);
				$sender_data["recepient_email"] = get_option("admin_email");
				
				if( isset($_POST["message"]) ){

					$sender_data["sender_message"] = strip_tags($_POST["message"]);

					if( sendMail($sender_data) ){
						$success = true;
						$status_msg .= "Email succesfuly sent.\n";
					} else {
						$success = false;
						$status_msg .= "Email has not been sent.\n";
					}

				} else {
					$success = false;
					$status_msg .= "No message body.\n";
				}
			} else {
				$success = false;
				$status_msg .= "Sender's email address is missing.\n";
			}
		} else {
			$success = false;
			$status_msg .= "Sender's name is missing.\n";
		}

		echo json_encode(array("success"=>$success, "data"=>$sender_data));
	}

	die();
}

add_action('wp_ajax_processContactFormData', 'processContactFormData'); //wp_ajax_processContactFormData comes from input type='hidden'
add_action('wp_ajax_nopriv_processContactFormData', 'processContactFormData');

function sendMail($sender_data){

	$header  = "Content-type:text/html;charset=UTF-8\r\n";
	$header .= "From: ".$sender_data['sender_name']." <".$sender_data['sender_email']. ">\r\n";
	$recipient = get_option("admin_email");

	$email_body =	"<html>".
						"<body style='font-family:Arial, Helvetica, sans-serif; font-size:14px; text-align:left;'>".
							"<img style='width:25%;' src='data:image/svg+xml;base64,'>".
							"<br><br>".
							"<h1>Hooray! A new message from {$sender_data['sender_name']}.</h1>".
							"<h3>His email is: {$sender_data['sender_email']}</h3>".
							"<p>{$sender_data['sender_message']}</p>".
						"</body>".
					"</html>";
		
	return mail($recipient, "New Contact From Mellow Pixels Website", $email_body, $header);
}


if ( ! function_exists( 'sydney_setup' ) ) :

/**
 * Sets up theme defaults and registers support for various WordPress features.
 *
 * Note that this function is hooked into the after_setup_theme hook, which
 * runs before the init hook. The init hook is too late for some features, such
 * as indicating support for post thumbnails.
 */


function sydney_setup() {

	/*
	 * Make theme available for translation.
	 * Translations can be filed in the /languages/ directory.
	 * If you're building a theme based on Sydney, use a find and replace
	 * to change 'sydney' to the name of your theme in all the template files
	 */
	load_theme_textdomain( 'sydney', get_template_directory() . '/languages' );

	// Add default posts and comments RSS feed links to head.
	add_theme_support( 'automatic-feed-links' );

	// Content width
	global $content_width;
	if ( ! isset( $content_width ) ) {
		$content_width = 1170; /* pixels */
	}

	/*
	 * Let WordPress manage the document title.
	 * By adding theme support, we declare that this theme does not use a
	 * hard-coded <title> tag in the document head, and expect WordPress to
	 * provide it for us.
	 */
	add_theme_support( 'title-tag' );

	/*
	 * Enable support for Post Thumbnails on posts and pages.
	 *
	 * @link http://codex.wordpress.org/Function_Reference/add_theme_support#Post_Thumbnails
	 */
	add_theme_support( 'post-thumbnails' );
	add_image_size('sydney-large-thumb', 830);
	add_image_size('sydney-medium-thumb', 550, 400, true);
	add_image_size('sydney-small-thumb', 230);
	add_image_size('sydney-service-thumb', 350);
	add_image_size('sydney-mas-thumb', 480);

	// This theme uses wp_nav_menu() in one location.
	register_nav_menus( array(
		'primary' => __( 'Primary Menu', 'sydney' ),
	) );

	/*
	 * Switch default core markup for search form, comment form, and comments
	 * to output valid HTML5.
	 */
	add_theme_support( 'html5', array(
		'search-form', 'comment-form', 'comment-list', 'gallery', 'caption',
	) );

	/*
	 * Enable support for Post Formats.
	 * See http://codex.wordpress.org/Post_Formats
	 */
	add_theme_support( 'post-formats', array(
		'aside', 'image', 'video', 'quote', 'link',
	) );

	// Set up the WordPress core custom background feature.
	add_theme_support( 'custom-background', apply_filters( 'sydney_custom_background_args', array(
		'default-color' => 'ffffff',
		'default-image' => '',
	) ) );
}
endif; // sydney_setup
add_action( 'after_setup_theme', 'sydney_setup' );

/**
 * Register widget area.
 *
 * @link http://codex.wordpress.org/Function_Reference/register_sidebar
 */
function sydney_widgets_init() {
	register_sidebar( array(
		'name'          => __( 'Sidebar', 'sydney' ),
		'id'            => 'sidebar-1',
		'description'   => '',
		'before_widget' => '<aside id="%1$s" class="widget %2$s">',
		'after_widget'  => '</aside>',
		'before_title'  => '<h3 class="widget-title">',
		'after_title'   => '</h3>',
	) );

	//Footer widget areas
	$widget_areas = get_theme_mod('footer_widget_areas', '3');
	for ($i=1; $i<=$widget_areas; $i++) {
		register_sidebar( array(
			'name'          => __( 'Footer ', 'sydney' ) . $i,
			'id'            => 'footer-' . $i,
			'description'   => '',
			'before_widget' => '<aside id="%1$s" class="widget %2$s">',
			'after_widget'  => '</aside>',
			'before_title'  => '<h3 class="widget-title">',
			'after_title'   => '</h3>',
		) );
	}

	//Register the front page widgets
	if ( function_exists('siteorigin_panels_activate') ) {
		register_widget( 'Sydney_List' );
		register_widget( 'Sydney_Services_Type_A' );
		register_widget( 'Sydney_Services_Type_B' );
		register_widget( 'Sydney_Facts' );
		register_widget( 'Sydney_Clients' );
		register_widget( 'Sydney_Testimonials' );
		register_widget( 'Sydney_Skills' );
		register_widget( 'Sydney_Action' );
		register_widget( 'Sydney_Video_Widget' );
		register_widget( 'Sydney_Social_Profile' );
		register_widget( 'Sydney_Employees' );
		register_widget( 'Sydney_Latest_News' );
		register_widget( 'Sydney_Contact_Info' );
		register_widget( 'Sydney_Portfolio' );
	}

}
add_action( 'widgets_init', 'sydney_widgets_init' );

